import React, { Component } from 'react';

export class RegistrationForm extends Component{
  state={
    Username: '',
    Password:''
  }
  onSubmit=(e)=>{
    e.preventDefault();
    this.props.RegistrationForm(this.state.title);
    this.setState({Username: '', Password:''})
  }
  getStyle=()=>{
    return{
      background: '#f4f4f4',
      padding: '10px',
      borderBotton: '1px #ccc dotted'
    }
  }


render(){
  return(
    <div>
    <h1>User Registration</h1>
    <p>Please fill in all the required fields to create a new user account.</p>
    <form onSubmit={this.handleSubmit}>
          <label>
            UserName:
            <textarea value={this.state.Username}  />
          </label>
          <label>
            Password:
            <textarea value={this.state.Password}  />
          </label>
          <input type="submit" value="Submit" />
        </form>
    </div>

  );

}
}

export default RegistrationForm;
